    [1362] = {id=1362,en="Indicolure Spell Effect Dur.",ja="インデ系魔法効果時間延長",endesc="Increases the effect duration of Indicolure spells.\nIncrease spell effect duration by 2 seconds.",jadesc="インデ系魔法の効果時間を延長します。\n能力値1で効果時間を+2秒します。"},
    [1408] = {id=1408,en="Elemental Sforzo Effect",ja="E.スフォルツォ効果アップ",endesc="Absorbs magic attacks while under the effects of Elemental Sforzo.\nIncrease the amount of damage absorbed by 2 percent.",jadesc="E.スフォルツォ中、魔法攻撃を吸収します。\n能力値1で吸収量を+2%します。"},
    [1410] = {id=1410,en="Rune Enchantment Effect",ja="エンチャントルーン効果アップ",endesc="Increases elemental resistance granted by each rune.\nIncrease resistance by 1.",jadesc="エンチャントルーンの属性耐性をアップします。\n能力値1で属性耐性を+1します。"},
    [1412] = {id=1412,en="Odyllic Subterfuge Effect",ja="オディリックサブタ効果アップ",endesc="Reduces Magic Atk. Bonus while under the effects of Odyllic Subterfuge.\nReduce Magic Atk. Bonus by 2.",jadesc="オディリックサブタ中、魔法攻撃力をダウンします。\n能力値1で魔法攻撃力を-2します。"},
    [1414] = {id=1414,en="Vallation Duration",ja="ヴァレション効果時間延長",endesc="Increases the effect duration of Vallation and Valiance.\nIncrease effect duration by 1 second.",jadesc="ヴァレションの効果時間を延長します。\n能力値1で効果時間を+1秒します。\nヴァリエンスも同様の効果を得ます。"},
    [1416] = {id=1416,en="Swordplay Effect",ja="ソードプレイ効果アップ",endesc="Increases the maximum amount of physical accuracy and physical evasion that may be raised by Swordplay.\nIncrease maximum amounts by 1.",jadesc="ソードプレイの物理命中と物理回避の上限値をアップ\nします。\n能力値1で物理命中と物理回避の上限を+1します。"},
    [1418] = {id=1418,en="Swipe Effect",ja="スワイプ効果アップ",endesc="Increases damage dealt with Swipe and Lunge.\nIncrease damage based on combat skills by 1 percent.",jadesc="スワイプのダメージをアップします。\n能力値1で武器スキルの+1%分アップします。\nランジも同様の効果を得ます。"},
    [1420] = {id=1420,en="Embolden Effect",ja="エンボルド効果アップ",endesc="Increases the effect of enhancing magic gained via Embolden.\nIncrease enhancing magic effect by 1 percent.",jadesc="エンボルドの効果を得た強化魔法の効果をアップ\nします。\n能力値1で強化魔法の効果を+1%します。"},
    [1422] = {id=1422,en="Vivacious Pulse",ja="ビベイシャス効果アップ",endesc="Increases the amount of HP restored through the divine magic skill when Vivacious Pulse is used.\nIncrease amount restored by 1 percent.",jadesc="ビベイシャス使用時、神聖魔法スキルによる回復量を\nアップします。\n能力値1で神聖魔法スキルによる回復量を+1%します"},
    [1424] = {id=1424,en="One for All Effect Duration",ja="ワンフォアオール効果時間延長",endesc="Increases the effect duration of One for All.\nIncrease effect duration by 1 second.",jadesc="ワンフォアオールの効果時間を延長します。\n能力値1で効果時間を+1秒します。"},
    [1426] = {id=1426,en="Gambit Effect Duration",ja="ガンビット効果時間延長",endesc="Increases the effect duration of Gambit.\nIncrease effect duration by 1 second.",jadesc="ガンビットの効果時間を延長します。\n能力値1で効果時間を+1秒します。"},
