    [3014] = {id=3014,en="Assimilation",ja="アシミレーション",endesc="Increases maximum blue magic points.\nIncrease blue magic point maximum by 1.",jadesc="青魔法ポイントの上限をアップ。\n能力値1で青魔法ポイントの上限を+1します。"},
    [3072] = {id=3072,en="Snake Eye",ja="スネークアイ",endesc="Your next roll will automatically be a 1. Recast: 5min.\nWhen current roll is 5 or higher, increase chance of rolling an 11 by 10%.",jadesc="次に振るロールの値を1にする。使用間隔:5分\n能力値1で現在のロールの値が5以上の時、11になる\nロールの値の出る確率が10%アップします。"},
    [3074] = {id=3074,en="Fold",ja="フォールド",endesc="Erases one roll or bust effect. Targets self-cast effect with the longest remaining duration. Recast: 5min.\nIncrease chance of resetting Phantom Roll recast timer by 10%.",jadesc="自身のかけたロール、及びバストの残り効果時間の\n長い方を1つ消す。使用間隔:5分\n能力値1でファントムロールの使用間隔をリセット\nする確率が10%アップします。"},
    [3076] = {id=3076,en="Winning Streak",ja="ウィニングストリーク",endesc="Extends the duration of Phantom Roll effects.\nIncrease duration of effect by 20 seconds.",jadesc="ファントムロールの効果時間を延長。\n能力値1で効果時間を20秒延長します。"},
    [3078] = {id=3078,en="Loaded Deck",ja="ローデッドデッキ",endesc="Focuses effect of Random Deal on abilities waiting on recast.\nHas a chance to fail if only one ability is waiting on recast.\nReduce chance of failure by 10%.",jadesc="ランダムディールの対象がリキャスト発生中のアビリティに絞られる。ただし、リキャスト発生中のアビリティが1つの場合はミスの可能性がある。\n能力値1でミスの確率を10%ダウンします。"},
    [3136] = {id=3136,en="Role Reversal",ja="黒衣チェンジ",endesc="Swaps HP of master and automaton. Recast: 2min.\nSupplement HP granted by 5%.",jadesc="マスターとオートマトンのHPを入れ替える。\n使用間隔:2分\n能力値1で入れ替えたHPを5%アップします。"},
    [3138] = {id=3138,en="Ventriloquy",ja="腹話術",endesc="Swaps the enmity of master and automaton for target. Recast: 1min.\nIncrease higher enmity value and decrease lower value by 5% each.",jadesc="対象モンスターの、マスターとオートマトンへの\n敵対心を入れ替える。使用間隔:1分\n能力値1で入れ替えた敵対心で高い方が5%アップ、\n低い方が5%ダウンします。"},
    [3140] = {id=3140,en="Fine-Tuning",ja="微調整",endesc="Grants a bonus to your automaton's accuracy, ranged accuracy, evasion, and magic defense.\nIncrease accuracy by 5, ranged accuracy by 5, evasion by 5, and magic defense by 5.",jadesc="オートマトンの命中、飛命、回避、魔法防御力にボーナスを得る。\n能力値1で命中を+5、飛命を+5、回避を+5、魔法防御力を+5します。"},
    [3142] = {id=3142,en="Optimization",ja="最適化",endesc="Grants a bonus to your automaton's attack, defense, and magic attack.\nIncrease attack by 5%, defense by 5%, and magic attack by 5.",jadesc="オートマトンの攻撃力、防御力、魔法攻撃力にボーナスを得る。\n能力値1で攻撃力を+5%、防御力を+5%、魔法攻撃力を+5します。"},
    [3200] = {id=3200,en="Saber Dance",ja="剣の舞い",endesc="Increases Double Attack rate, but renders waltzes unusable. Double Attack rate gradually decreases. Recast: 3min.\nIncrease samba duration by 5%.",jadesc="効果中、ダブルアタック発動率アップ、ワルツ使用\n不可。ダブルアタック発動率は徐々に減衰する。\n使用間隔:3分\n能力値1でサンバの効果時間を5%延長します。"},
    [3202] = {id=3202,en="Fan Dance",ja="扇の舞い",endesc="Reduces physical damage taken and increases enmity, but renders sambas unusable. Physical damage reduction gradually decreases with each hit taken. Recast: 3min.\nShorten waltz recast time by 5%.",jadesc="効果中、被物理ダメージカット＆敵対心アップ、サ\nンバ使用不可。被物理ダメージカット量は相手から\nの攻撃を受ける毎に徐々に減衰する。使用間隔:3分\n能力値1でワルツの使用間隔を5%短縮します。"},
    [3204] = {id=3204,en="No Foot Rise",ja="ノーフットライズ",endesc="Instantly grants additional finishing moves. Recast: 3min.\nIncrease finishing moves granted by 1.",jadesc="フィニシングムーブを貯める。使用間隔:3分\n能力値1でフィニシングムーブを+1します。"},
    [3206] = {id=3206,en="Closed Position",ja="Ｃ・ポジション",endesc="Grants a bonus to accuracy and evasion when face-to-face with an enemy.\nIncrease accuracy and evasion bonuses\nby 3.",jadesc="敵の正面にいる場合に限り、命中と回避にボーナスを得る。\n能力値1で命中を+3、回避を+3します。"},
    [3264] = {id=3264,en="Altruism",ja="不惜身命の章",endesc="Light Arts Stratagem.\nIncreases the accuracy of your next white magic spell.\nIncrease magic accuracy by 5.",jadesc="白のグリモア戦術魔道書アビリティ。\n次に唱える白魔法の命中率がアップ。\n能力値1で魔法命中率を+5します。"},
    [3266] = {id=3266,en="Focalization",ja="一心精進の章",endesc="Dark Arts Stratagem.\nIncreases the accuracy of your next black magic spell.\nIncrease magic accuracy by 5.",jadesc="黒のグリモア戦術魔道書アビリティ。\n次に唱える黒魔法の命中率がアップ。\n能力値1で魔法命中率を+5します。"},
    [3268] = {id=3268,en="Tranquility",ja="天衣無縫の章",endesc="Light Arts Stratagem.\nYour next white magic spell will generate less enmity.\nReduce enmity by 10.",jadesc="白のグリモア戦術魔道書アビリティ。\n次に唱える白魔法の敵対心をダウン。\n能力値1で敵対心を-10します。"},
    [3270] = {id=3270,en="Equanimity",ja="無憂無風の章",endesc="Dark Arts Stratagem.\nYour next black magic spell will generate less enmity.\nReduce enmity by 10.",jadesc="黒のグリモア戦術魔道書アビリティ。\n次に唱える黒魔法の敵対心をダウン。\n能力値1で敵対心を-10します。"},
    [3272] = {id=3272,en="Enlightenment",ja="大悟徹底",endesc="Optimizes both white and black magic capabilities and allows access to both addenda for your next spell. Recast: 5min.\n+5 bonus to INT and MND.",jadesc="グリモアに関わらず次の1回だけ白魔法、黒魔法とも\nに特化し、両方の補遺の魔法を詠唱可能。\n使用間隔:5分\n能力値1でINTとMNDを+5します。"},
    [3274] = {id=3274,en="Stormsurge",ja="陣頭指揮",endesc="Storm-type spells grant a bonus to attributes associated with their element. Initial bonus value: +3.\nIncrease bonus value by 1.",jadesc="陣の効果に、天候の属性に対応するステータスアップ効果を付与する。初期値:ステータス+3\n能力値1でステータスを+1します。"},
    [3392] = {id=3392,en="Mending Halation",ja="メンドハレイション",endesc="Causes your luopan to vanish and \nrestores HP of party members within area of effect. \nRecast: 5min. Increase amount of HP restored by 5 percent.",jadesc="羅盤を消失し、範囲内のパーティメンバーのHPを回\n復する。使用間隔:5分\n能力値1でHP回復量を5%アップします。"},
    [3394] = {id=3394,en="Radial Arcana",ja="レイディアルアルカナ",endesc="Causes your luopan to vanish and \nrestores MP of party members within area of effect. \nRecast: 5min. Increase amount of MP restored by 3 percent.",jadesc="羅盤を消失し、範囲内のパーティメンバーのMPを回\n復する。使用間隔:5分\n能力値1でMP回復量を3%アップします。"},
    [3396] = {id=3396,en="Curative Recantation",ja="キュアリカント",endesc="Grants a healing effect to Full Circle. \nIncrease amount of HP healed by 5 percent.",jadesc="フルサークルにHP回復の効果を得る。\n能力値1でHP回復量を5%アップします。"},
    [3398] = {id=3398,en="Primeval Zeal",ja="プライムジール",endesc="Shortens casting times of elemental magic spells while under the effects of Collimated Fervor. \nShorten cast time by 10 percent.",jadesc="コリメイトファーバー中、精霊魔法の詠唱時間短縮の\n効果を得る。能力値1で詠唱時間を10%短縮します。"},
    [3456] = {id=3456,en="Battuta",ja="バットゥタ",endesc="Increases the likelihood of parrying and deals counter damage after parrying dependent upon harbored runes. \nRecast: 5min. Increase chance of parrying by 4 percent and increase counter damage by 4 points per rune harbored.",jadesc="受け流し確率にボーナスを得る。\nさらに、受け流し発動時エンチャントルーンに依存し\nた反撃ダメージを与える。使用間隔:5分。\n能力値1で受け流し確率を4%アップ、反撃ダメージを\nエンチャントルーン１つにつき+4アップします。"},
    [3458] = {id=3458,en="Rayke",ja="レイク",endesc="Expends runes to reduce elemental resistance of the target. \nRecast: 5min. Increase duration of effect by 3 seconds.",jadesc="ルーンを消費して、対象の属性耐性をダウンさせる。\n使用間隔:5分。\n能力値1で効果時間を3秒延長します。"},
    [3460] = {id=3460,en="Inspiration",ja="インスパイア",endesc="Grants a \"Fast Cast\" effect to Vallation and Valiance. \nIncrease cast speed by 10 percent.",jadesc="ヴァレション、ヴァリエンスに魔法詠唱時間短縮の効\n果を得る。\n能力値1で魔法詠唱時間を10%短縮します。"},
    [3462] = {id=3462,en="Sleight of Sword",ja="スライトソード",endesc="Grants a \"Subtle Blow\" effect during Swordplay. \nIncrease the maximum value of Subtle Blow by 5 points.",jadesc="ソードプレイ中、モクシャの効果を得る。\n能力値1でモクシャの効果の最大値を5アップします。"},
}, {"id", "en", "ja", "endesc", "jadesc"}

